from django import forms# Features that reduce the workload, no front-end to writeclass Register_form(forms.Form):    username = forms.CharField(required=True, min_length=4, max_length=15, error_messages={        'min_length': 'Username length cannot list 4',        'max_length': 'Username length cannot be greater than 1',        'require': 'Username cannot be empty',    })    password = forms.CharField(min_length=6, required=True, error_messages={        'min_length': 'Password length cannot be less than 6',        'require': 'Password cannot be empty',    })    email = forms.EmailField()# user login formclass Login_form(forms.Form):    username = forms.CharField(required=True, min_length=6, max_length=15, error_messages={        'min_length': 'Username length cannot be less than 6',        'max_length': 'Username length cannot be greater than 15',        'require': 'Username cannot be empty',    })    password = forms.CharField(min_length=6, max_length=30, required=True, error_messages={        'min_length': 'Password length cannot be less than 6',        'require': 'Password cannot be empty',    })