# coding: utf-8from django.urls import path, includefrom rest_framework.routers import DefaultRouterfrom . import viewsfrom Bytickey.views import indexrouter = DefaultRouter()router.register('seat', views.SeatView)router.register('order', views.OrderView)urlpatterns = [    path('login/', views.login, name='login'),    path('register/', views.register, name='register'),    path('user_setting/', views.user_setting, name='user_setting'),    path('logout/', views.loginout, name='logout'),    path('seat_selection/', views.seat_selection, name='check_seat'),    path('add_Ticket/', views.add_Ticket, name='choice_Ticket'),    path('manage/', views.manage, name='manage'),    path('manage_do/', views.manage_do, name='manage_do'),    path('modal_data_select/', views.modal_data_select, name='modal_data_select'),    path('booking_display/', views.booking_display, name='booking_display'),    path('index/', index, name='index'),    path('api/', include(router.urls))]